// Role-based field filtering for Speaker data
const role = $('GET /speakers').first().json.query?.role || 'GUEST';
const items = $('Read Speaker_DB').all();

const SENSITIVE_FIELDS = ['E-Mail', 'Telefon', 'Webseite', 'Registriert_von'];

const filtered = items
  .filter(item => {
    const status = (item.json.Status || '').toLowerCase();
    return status === 'aktiv' || status === 'dummy';
  })
  .map(item => {
    const speaker = { ...item.json };

    // Strip sensitive fields for non-admin roles
    if (role !== 'ADMIN') {
      SENSITIVE_FIELDS.forEach(field => delete speaker[field]);
      // Mask last name for non-admin (show only initial)
      if (speaker.Nachname && speaker.Nachname.length > 0) {
        speaker.Nachname = speaker.Nachname[0] + '.';
      }
    }

    return { json: speaker };
  });

return filtered;

// Generate Speaker ID and validate input
const body = $input.first().json.body;

// Dummies only need vorname, real speakers need vorname + email
if (body.isDummy && !body.vorname) {
  return [{ json: { error: 'Bitte Platzhalter-Namen eingeben', status: 400 } }];
}
if (!body.isDummy && (!body.vorname || !body.email)) {
  return [{ json: { error: 'Vorname und E-Mail sind Pflichtfelder', status: 400 } }];
}

const timestamp = new Date().toISOString();
const randomPart = Math.random().toString(36).substring(2, 6).toUpperCase();
const id = `SPK-${randomPart}`;

return [{
  json: {
    ID: id,
    Vorname: body.vorname,
    Nachname: body.nachname || '',
    'E-Mail': body.email || '',
    Telefon: body.telefon || '',
    Bio: body.bio || '',
    Organisation: body.organisation || '',
    Webseite: body.webseite || '',
    Pronomen: body.pronomen || '',
    Status_Einladung: body.isDummy ? 'dummy' : 'CFP',
    Registriert_am: timestamp,
    Registriert_von: body.registeredBy || 'self',
    _id: id
  }
}];

// Validate and prepare session submission
const body = $input.first().json.body;

if (!body.titel || !body.submitterEmail) {
  return [{ json: { error: 'Titel und Submitter-E-Mail sind Pflichtfelder', status: 400 } }];
}

const timestamp = new Date().toISOString();
const idx = Math.random().toString(36).substring(2, 6).toUpperCase();
const sessionId = `ANTIGRAV-${idx}`;

// Check for dummy speakers (IDs starting with TBD)
const speakerIds = (body.speakerIds || []);
const dummySpeakers = speakerIds.filter(id => id.startsWith('TBD') || id === '');

return [{
  json: {
    Zeitstempel: timestamp,
    'E-Mail-Adresse': body.submitterEmail,
    Vorname: body.submitterVorname || '',
    Nachname: body.submitterNachname || '',
    'Session-Titel': body.titel,
    Kurzbeschreibung: body.kurzbeschreibung || '',
    Beschreibung: body.beschreibung || '',
    Format: body.format || 'Talk',
    Thema: body.thema || '',
    Bereich: body.bereich || '',
    Sprache: body.sprache || 'DE',
    'Dauer (Minuten)': body.dauer || 60,
    'Co-Speaker': speakerIds.join(', '),
    Bio: body.bio || '',
    'Webseite': body.webseite || '',
    _sessionId: sessionId,
    _hasDummies: dummySpeakers.length > 0
  }
}];

const body = $('Webhook (POST /api/rating)').first().json.body;
let email = '';
try { email = $('Verify Google Token').first().json.email; } catch(e) {}
if (!email) { try { email = $('Verify Magic JWT (Rating)').first().json.email; } catch(e) {} }
return [{ json: {
  'Zeitstempel': new Date().toISOString(),
  'Session_ID': body.sessionId || '',
  'Reviewer_Email': email,
  'Score': body.score || 0,
  'Kommentar': body.kommentar || '',
  'Kategorie': body.kategorie || 'relevanz'
}}];

// WICHTIG: Die Feld-Namen müssen EXAKT mit den Sheet-Headern übereinstimmen!
// Sheet-Header: Status_Einladung | Status_Backend | ID | Vorname | Nachname | Pronomen | Organisation | Bio | Webseite | Update | E-Mail | Telefon | Herkunft | Sprache | Registriert_am | Registriert_von

const timestamp = Date.now().toString(36).toUpperCase();
const random = Math.random().toString(36).substring(2, 5).toUpperCase();
const speakerId = `SPK-${timestamp.slice(-4)}${random}`;

const body = $input.first().json.body || {};
const vorname = (body.vorname || '').trim();
const nachname = (body.nachname || '').trim();
const email = (body.email || '').trim();
const bio = (body.bio || '').trim();

const isComplete = email && bio && vorname && nachname;
const status = isComplete ? '8_CFP' : '9_CFP_Dummy';
const registriertVon = body.registriert_von || 'CFP-Formular';

// Feld-Namen = Sheet-Header (exakt!)
return [{
  json: {
    'Status_Einladung': status,
    'Status_Backend': 'aktiv',
    'ID': speakerId,
    'Vorname': vorname,
    'Nachname': nachname,
    'Pronomen': body.pronomen || '',
    'Organisation': body.organisation || '',
    'Bio': bio,
    'Webseite': body.webseite || '',
    'Update': '',
    'E-Mail': email,
    'Telefon': body.telefon || '',
    'Herkunft': body.herkunft || '',
    'Sprache': body.sprache || '',
    'Registriert_am': new Date().toISOString(),
    'Registriert_von': registriertVon
  }
}];

const jwt = require('jsonwebtoken');
const SECRET = '537f8c4ff1959fdc1d54ce852607f2c30f08607925a39e61be189d3d58e29ff1';
const token = $input.first().json.body?.token || '';
if (!token) {
  return [{ json: { ok: false, error: 'Kein Token angegeben.' } }];
}
try {
  const decoded = jwt.verify(token, SECRET);
  if (!decoded.nonce) {
    return [{ json: { ok: false, error: 'Ungültiges Token-Format.' } }];
  }
  return [{ json: { valid: true, email: decoded.email, role: decoded.role || 'TEILNEHMENDE', nonce: decoded.nonce }}];
} catch (err) {
  if (err.name === 'TokenExpiredError') {
    return [{ json: { ok: false, error: 'Der Magic Link ist abgelaufen.' } }];
  }
  return [{ json: { ok: false, error: 'Der Magic Link ist ungültig.' } }];
}

const verifyData = $('Verify JWT').first().json;
const nonce = String(verifyData.nonce || '').trim();
const email = verifyData.email;
const rows = $input.all().map(item => item.json);
let tokenRow = null;
let tokenRowIndex = -1;
for (let i = 0; i < rows.length; i++) {
  const vals = Object.values(rows[i]);
  if (vals.some(v => String(v).trim() === nonce)) {
    tokenRow = rows[i];
    tokenRowIndex = i + 2;
    break;
  }
}
if (!tokenRow) {
  return [{ json: { ok: false, nonceValid: false, error: 'Magic Link ungueltig.' }}];
}
const statusVal = Object.values(tokenRow).find(v => String(v).trim().toUpperCase() === 'USED');
if (statusVal) {
  return [{ json: { ok: false, nonceValid: false, error: 'Magic Link bereits verwendet.' }}];
}
return [{ json: { ok: true, nonceValid: true, email: email, role: verifyData.role, tokenRowIndex: tokenRowIndex, nonce: nonce }}];

// Check if user exists in Config_Users, create if new
const nonceData = $('Check Nonce (One-Time)').first().json;
const email = (nonceData.email || '').toLowerCase().trim();
const tokenRole = nonceData.role;

const rows = $input.all().map(item => item.json);

// Search ALL column values for the email (handles any column naming)
const existingUser = rows.find(r => {
  return Object.values(r).some(v => 
    String(v || '').toLowerCase().trim() === email
  );
});

if (existingUser) {
  // Find the role value (second column typically)
  const vals = Object.values(existingUser);
  const roleVal = vals.find(v => 
    ['ADMIN','CURATOR','REVIEWER','SPEAKER','TEILNEHMENDE','PRODUCTION','PARTNER','BAND','GUEST'].includes(String(v).toUpperCase().trim())
  );
  return [{ json: {
    email,
    role: roleVal ? String(roleVal).trim() : tokenRole,
    isNewUser: false,
    needsCreation: false
  }}];
} else {
  return [{ json: {
    email,
    role: tokenRole || 'TEILNEHMENDE',
    isNewUser: true,
    needsCreation: true
  }}];
}

// Extract input
const email = ($input.first().json.body?.email || '').toLowerCase().trim();
const adminInvite = $input.first().json.body?.adminInvite === true;

if (!email || !email.includes('@')) {
  return [{ json: { ok: false, error: 'Bitte gib eine gültige Email-Adresse ein.' } }];
}

return [{ json: { email, adminInvite } }];

// Check open call status and user existence
const inputData = $('Parse Input').first().json;
const email = inputData.email;
const adminInvite = inputData.adminInvite;

const rows = $input.all().map(item => item.json);

// Open Call status check
let openCallStatus = 'OPEN';
if (rows.length > 0) {
  const firstRow = rows[0];
  const dValue = firstRow['D'] || firstRow['Status'] || firstRow['OpenCall'] || '';
  if (dValue.toUpperCase() === 'CLOSED') {
    openCallStatus = 'CLOSED';
  }
}

// Find user by email
const existingUser = rows.find(r => {
  const rowEmail = (r['A'] || r['Email'] || r['email'] || '').toLowerCase().trim();
  return rowEmail === email;
});

const existingRole = existingUser ? (existingUser['B'] || existingUser['Role'] || existingUser['role'] || 'TEILNEHMENDE') : null;

// Decision logic
if (!adminInvite && openCallStatus === 'CLOSED' && !existingUser) {
  return [{ json: {
    allowed: false,
    error: 'Der Open Call ist derzeit geschlossen. Nur eingeladene Personen können sich anmelden. Bitte wende dich an das Admin-Team.'
  }}];
}

return [{ json: {
  allowed: true,
  email: email,
  role: existingRole || 'TEILNEHMENDE',
  isExistingUser: !!existingUser,
  adminInvite: adminInvite
}}];

const jwt = require('jsonwebtoken');
const SECRET = '537f8c4ff1959fdc1d54ce852607f2c30f08607925a39e61be189d3d58e29ff1';
const email = $json.email;
const role = $json.role;
function generateNonce() {
  const chars = 'abcdef0123456789';
  let result = Date.now().toString(16);
  for (let i = result.length; i < 48; i++) {
    result += chars[Math.floor(Math.random() * chars.length)];
  }
  return result;
}
const nonce = generateNonce();
const token = jwt.sign({ email, role, nonce }, SECRET, { expiresIn: '7d' });
const SITE_URL = 'https://tourmaline-taffy-61d3c1.netlify.app';
const magicLink = `${SITE_URL}/?magic=${token}`;
const expiresAt = new Date(Date.now() + 7 * 24 * 60 * 60 * 1000).toISOString();
return [{ json: { email, role, token, nonce, magicLink, expiresAt, isExistingUser: $json.isExistingUser }}];

const jwt = require('jsonwebtoken');
