const authHeader = $('Webhook (POST /api/data)').first().json.headers?.authorization || '';
const token = authHeader.replace(/^Bearer\\s+/i, '').trim();
if (!token) return [{ json: { error: 'No token' } }];
try {
  const decoded = jwt.verify(token, SECRET);
  if (decoded.email) return [{ json: { email: decoded.email, role: decoded.role || 'TEILNEHMENDE', authType: 'magic' } }];
} catch(e) {}
return [{ json: { error: 'Invalid token' } }];

